import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

export default function RecipeReviewCard() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (

<div class="container">
  <div class="row">
    <div class="col-sm">
    <Card className={classes.root}>
      <CardHeader
        // avatar={
        //   <Avatar aria-label="recipe" className={classes.avatar}>
        //     R
        //   </Avatar>
        // }
        // action={
        //   <IconButton aria-label="settings">
        //     <MoreVertIcon />
        //   </IconButton>
        // }
        // title="Ellesse"
        // subheader="September 14, 2020"
      />
      <CardMedia
        className={classes.media}
        image="https://az1.hatstoremedia.com/hatstore/images/5057677585236_1/652/522/0/lorenzo-white-bucket-ellesse.jpg"
        title="ellesse"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
        Lorenzo White Bucket - Ellesse a bit more text here
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        {/* <IconButton aria-label="share">
          <ShareIcon />
        </IconButton> */}
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography paragraph>
            Description of the hat.
          </Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography>
          Description of the hat.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
    </div>
    <div class="col-sm">
    <Card className={classes.root}>
      <CardHeader
        // avatar={
        //   <Avatar aria-label="recipe" className={classes.avatar}>
        //     R
        //   </Avatar>
        // }
        // action={
        //   <IconButton aria-label="settings">
        //     <MoreVertIcon />
        //   </IconButton>
        // }
        // title="Ellesse"
        // subheader="September 14, 2020"
      />
      <CardMedia
        className={classes.media}
        image="https://az2.hatstoremedia.com/hatstore/images/193650915784_1/652/522/0/gore-tex-black-white-bucket-new-era.jpg"
        title="New Era"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
        GORE-TEX Black/White Bucket - New Era
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        {/* <IconButton aria-label="share">
          <ShareIcon />
        </IconButton> */}
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography>
          Description of the hat.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
    </div>
    <div class="col-sm">
    <Card className={classes.root}>
      <CardHeader
        // avatar={
        //   <Avatar aria-label="recipe" className={classes.avatar}>
        //     R
        //   </Avatar>
        // }
        // action={
        //   <IconButton aria-label="settings">
        //     <MoreVertIcon />
        //   </IconButton>
        // }
        // title="Ellesse"
        // subheader="September 14, 2020"
      />
      <CardMedia
        className={classes.media}
        image="https://az2.hatstoremedia.com/hatstore/images/828432257102_1/652/522/0/cooperman-woodland-camo-bucket-herschel.jpg"
        title="Herschel"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
        Cooperman Woodland Camo Bucket - Herschel
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        {/* <IconButton aria-label="share">
          <ShareIcon />
        </IconButton> */}
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
            minutes.
          </Typography>
          <Typography paragraph>
          Description of the hat.          </Typography>
          <Typography paragraph>
          Description of the hat.
          </Typography>
          <Typography>
          Description of the hat.          </Typography>
        </CardContent>
      </Collapse>
    </Card>
    </div>
  </div>
</div>





  );
}
